package com.lesdo.im.packetExtension;import org.jivesoftware.smack.packet.PacketExtension;/** * 发送人头像图片ID *  * @author yangxiaojun * */public class HeadImageIDExtension implements PacketExtension {	private String headImageID;	public String getHeadImageID() {		return headImageID;	}	public void setHeadImageID(String address) {		this.headImageID = address;	}	public static final String NAME = "headImageID";	public static final String NAME_SPACE = "jabber:client";	public HeadImageIDExtension() {		super();	}	public String getElementName() {		return NAME;	}	public String getNamespace() {		return NAME_SPACE;	}	public String toXML() {		StringBuffer sb = new StringBuffer();		sb.append("<"+NAME+"").append(" xmlns=\"").append(NAME_SPACE)				.append("\">").append(headImageID);		sb.append("</"+NAME+">");		return sb.toString();	}}