package com.lesdo.im.packetExtension;import org.jivesoftware.smack.packet.PacketExtension;/** * 发送人昵称 *  * @author yangxiaojun * */public class NickNameExtension implements PacketExtension {	private String nickName;	public String getNickName() {		return nickName;	}	public void setNickName(String address) {		this.nickName = address;	}	public static final String NAME = "nickName";	public static final String NAME_SPACE = "jabber:client";	public NickNameExtension() {		super();	}	public String getElementName() {		return NAME;	}	public String getNamespace() {		return NAME_SPACE;	}	public String toXML() {		StringBuffer sb = new StringBuffer();		sb.append("<"+NAME+"").append(" xmlns=\"").append(NAME_SPACE)				.append("\">").append(nickName);		sb.append("</"+NAME+">");		return sb.toString();	}}