package com.lesdo.im.packetExtension;import org.jivesoftware.smack.packet.PacketExtension;/** * 发送时间 *  * @author yangxiaojun *  */public class SendTimeExtension implements PacketExtension {	private Long sendTime;	public Long getSendTime() {		return sendTime;	}	public void setSendTime(Long sendTime) {		this.sendTime = sendTime;	}	public static final String NAME = "sendTime";	public static final String NAME_SPACE = "jabber:client";	public SendTimeExtension() {		super();	}	public String getElementName() {		return NAME;	}	public String getNamespace() {		return NAME_SPACE;	}	public String toXML() {		StringBuffer sb = new StringBuffer();		sb.append("<" + NAME + "").append(" xmlns=\"").append(NAME_SPACE)				.append("\">").append(sendTime);		sb.append("</" + NAME + ">");		return sb.toString();	}}